import React, { useState, useEffect } from "react";
import axios from "axios";

function TransactionsList() {
  const [data, setData] = useState([]);
  const [customer, setCustomer] = useState([]);
  const [expenseType, setExpenseType] = useState([]);
  const [transactionType, setTransactionType] = useState([]);
  const [edit_id, setEditID] = useState("");
  useEffect(() => {
    axios
      .get(`http://localhost:8000/transaction/`)
      .then((result) => setData(result.data))
      .catch((error) => console.log("Error", error));

    axios
      .get(`http://localhost:8000/expense-type/`)
      .then((result) => setExpenseType(result.data))
      .catch((error) => console.log("Error", error));

    axios
      .get(`http://localhost:8000/transaction-type/`)
      .then((result) => setTransactionType(result.data))
      .catch((error) => console.log("Error", error));

    axios
      .get(`http://localhost:8000/customer/`)
      .then((result) => setCustomer(result.data))
      .catch((error) => console.log("Error", error));
  }, []);
  return (
    <div>
      <div class="flex items-center justify-center min-h-screen bg-indigo-300">
        <div class="col-span-12">
          <div class="overflow-auto lg:overflow-visible ">
            <table class="table text-gray-800 border-separate space-y-6 text-sm">
              <thead class="bg-indigo-200 text-gray-900">
                <tr>
                  <th class="p-3 text-left">To</th>
                  <th class="p-3 text-left">Transaction Type</th>
                  <th class="p-3 text-left">Amount</th>
                  <th class="p-3 text-left">Expense Category</th>
                  <th class="p-3 text-left">Action</th>
                </tr>
              </thead>
              <tbody>
                {data?.map((item) => {
                  {
                    customer === [] ? (
                      <h1>if the customer is empty array</h1>
                    ) : (
                      <h1>if the customer array has something </h1>
                    );
                  }
                  //to make that you have to make {cust|| <h1> Text </h1>} that means if there is no cust then show the header
