import React, { useState } from "react";

function TransactionForm() {
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [type, setType] = useState("");
  const [ref, setRef] = useState("");
  const handleSubmit = async (e) => {
    e.preventDefault();
    const transaction = {
      sender: from,
      receiver: to,
      ttype: type,
      reference: ref,
    };
    console.log(transaction);
  };

  return (
    <div>
      <div class="h-screen bg-indigo-100 flex justify-center items-center">
        <div class="lg:w-2/5 md:w-1/2 w-2/3">
          <form class="bg-white p-10 rounded-lg shadow-lg min-w-full">
            <h1 class="text-center text-2xl mb-6 text-gray-600 font-bold font-sans">
              New Transaction
            </h1>
            <div>
              <label
                class="text-gray-800 font-semibold block my-3 text-md"
                for="from"
              >
                From
              </label>
              <input
                value={from}
                onChange={(e) => {
                  setFrom(e.target.value);
                  console.log(from);
                }}
                name="from"
                id="from"
                class="w-full bg-gray-100 px-4 py-2 rounded-lg focus:outline-none"
                type="text"
                placeholder="from"
              />
            </div>
            <div>
              <label
                class="text-gray-800 font-semibold block my-3 text-md"
                for="to"
              >
                To
              </label>
              <input
                value={to}
                onChange={(e) => {
                  setTo(e.target.value);
                }}
                name="to"
                id="to"
                class="w-full bg-gray-100 px-4 py-2 rounded-lg focus:outline-none"
                type="text"
                placeholder="To"
              />
            </div>
            <div>
              <label
                class="text-gray-800 font-semibold block my-3 text-md"
                for="transaction_type"
              >
                Transaction Type
              </label>
              <select
                className="w-full bg-gray-100 px-4 py-2 rounded-lg focus:outline-none"
                required
                value={type}
                name="transaction_type"
                id="transaction_type"
                placeholder="normal"
                onChange={(e) => setType(e.target.value)}
              >
                <option value="normal">Normal Transaction</option>
                <option value="foreign">Foreign Transaction</option>
                <option value="repeating">Repeating Transaction</option>
                <option value="self">Self Transaction</option>
              </select>
            </div>
            <div>
              <label
                class="text-gray-800 font-semibold block my-3 text-md"
                for="transaction_reference"
              >
                Transaction Reference
              </label>
              <input
                value={ref}
                onChange={(e) => {
                  setRef(e.target.value);
                }}
                class="w-full bg-gray-100 px-4 py-2 rounded-lg focus:outline-none"
                type="text"
                name="transaction_reference"
                id="transaction_reference"
                placeholder="Transaction Reference"
              />
            </div>
            <button
              type="submit"
              onClick={(e) => handleSubmit(e)}
              class="w-full mt-6 bg-indigo-600 rounded-lg px-4 py-2 text-lg text-white tracking-wide font-semibold font-sans"
            >
              Submit
            </button>
            <button
              type="submit"
              class="w-full mt-6 mb-3 bg-indigo-100 rounded-lg px-4 py-2 text-lg text-gray-800 tracking-wide font-semibold font-sans"
            >
              Clear
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default TransactionForm;
