import React, { useState, useEffect } from "react";
import axios from "axios";

function TransactionsList() {
  const [data, setData] = useState([]);
  const [expenseType, setExpenseType] = useState([]);
  const [edit_id, setEditID] = useState("");
  useEffect(() => {
    axios
      .get(`http://localhost:8000/transaction/`)
      .then((result) => {
        setData(result.data);
      })
      .catch((error) => {
        console.log("Error", error);
      });

    axios
      .get(`http://localhost:8000/expense-type/`)
      .then((result) => setExpenseType(result.data))
      .catch((error) => console.log("Error", error));

    axios
      .get(`http://localhost:8000/expense-type/`)
      .then((result) => setExpenseType(result.data))
      .catch((error) => console.log("Error", error));
  }, []);
  return (
    <div>
      <div class="flex items-center justify-center min-h-screen bg-indigo-300">
        <div class="col-span-12">
          <div class="overflow-auto lg:overflow-visible ">
            <table class="table text-gray-800 border-separate space-y-6 text-sm">
              <thead class="bg-indigo-200 text-gray-900">
                <tr>
                  <th class="p-3 text-left">To</th>
                  <th class="p-3 text-left">Transaction Type</th>
                  <th class="p-3 text-left">Amount</th>
                  <th class="p-3 text-left">Expense Category</th>
                  <th class="p-3 text-left">Action</th>
                </tr>
              </thead>
              <tbody>
                {data?.map((item) => {
                  return (
                    <tr key={item.id} class="bg-indigo-200">
                      <td class="p-3">
                        <div class="flex align-items-center">
                          <div class="ml-3">
                            <div class="">
                              {item.sender_account} {item.receiver_account}
                            </div>
                            <div class="text-gray-500">
                              {item.transaction_type}
                            </div>
                            <div class="text-gray-500">
                              {item.payment_reference}
                            </div>
                          </div>
                        </div>
                      </td>
                      <td class="p-3">{item.transaction_type}</td>
                      <td class="p-3 font-bold">{item.amount} â‚¬</td>
                      <td class="p-3">
                        <span class="bg-green-400 text-gray-50 rounded-md px-2">
                          {item.transaction_limit}
                        </span>
                      </td>
                      <td class="p-3 ">
                        <a
                          href="#"
                          class="text-gray-500 hover:text-gray-100  mx-2"
                        >
                          <button
                            class="material-icons-outlined text-base"
                            onClick={() => setEditID(item.id)}
                          >
                            edit
                          </button>
                        </a>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TransactionsList;
