import React, { useState, useEffect } from "react";
import { iban } from "../Dummy/Constants";
import axios from "axios";

function TransactionForm() {
  const [ibanNo, setIbanNo] = useState(iban);
  const [to, setTo] = useState("");
  const [type, setType] = useState("");
  const [ref, setRef] = useState("");
  const [amount, setAmount] = useState("");

  const [customer, setCustomer] = useState("");
  const [ExpenseTypes, setExpenseTypes] = useState("");
  const [transactionTypes, setTransactionTypes] = useState("");
  const [customers, setCustomers] = useState("");
  const [accounts, setAccounts] = useState({});
  const handleSubmit = async (e) => {
    e.preventDefault();
    const transaction = {
      sender: ibanNo,
      receiver: to,
      ttype: type,
      reference: ref,
      amount: amount,
    };
    console.log(transaction);
  };

  useEffect(() => {
    axios
      .get(`http://localhost:8000/expense-type/`)
      .then((result) => setExpenseTypes(result.data))
      .catch((error) => console.log("Error", error));

    axios
      .get(`http://localhost:8000/transaction-type/`)
      .then((result) => setTransactionTypes(result.data))
      .catch((error) => console.log("Error", error));

    axios
      .get(`http://localhost:8000/customer/1/`)
      .then((result) => setCustomer(result.data))
      .catch((error) => console.log("Error", error));

    axios
      .get(`http://localhost:8000/account/`)
      .then((result) => setAccounts(result.data))
      .catch((error) => console.log("Error", error));

    axios
      .get(`http://localhost:8000/customer/`)
      .then((result) => setCustomers(result.data))
      .catch((error) => console.log("Error", error));
  }, []);

  return (
    <div>
      <div class="h-screen bg-indigo-300 flex justify-center items-center">
        <div class="lg:w-2/5 md:w-1/2 w-2/3">
          <form class="bg-indigo-200 p-10 rounded-lg shadow-lg min-w-full">
            <h1 class="text-center text-2xl mb-3 text-gray-600 font-bold font-sans">
              Create a new transaction
            </h1>
            <div>
              <label
                class="text-gray-800 font-semibold block mt-3 text-md"
                for="from"
              >
                From
              </label>
              <select
                className="w-full bg-indigo-100 px-4 py-2 h-8 rounded-lg focus:outline-none"
                required
                value={ibanNo}
                name="from"
                id="from"
                placeholder="from"
                onChange={(e) => setIbanNo(e.target.value)}
              >
                {accounts?.map((element) => {
                  return <option value={element.iban}>{element.iban}</option>;
                })}
              </select>
            </div>
            <div>
              <label
                class="text-gray-800 font-semibold block mt-3 text-md"
                for="to"
              >
                To
              </label>
              <input
                value={to}
                onChange={(e) => {
                  setTo(e.target.value);
                }}
                name="to"
                id="to"
                class="w-full bg-indigo-100 px-4 py-1 rounded-lg focus:outline-none"
                type="text"
                placeholder="To"
              />
            </div>
            <div>
              <label
                class="text-gray-800 font-semibold block mt-3 text-md"
                for="transaction_type"
              >
                Transaction Type
              </label>
              <select
                className="w-full bg-indigo-100 px-4 py-2 h-8 rounded-lg focus:outline-none"
                required
                value={type}
                name="transaction_type"
                id="transaction_type"
                placeholder="normal"
                onChange={(e) => setType(e.target.value)}
              >
                <option value="normal">Normal Transaction</option>
                <option value="foreign">Foreign Transaction</option>
                <option value="repeating">Repeating Transaction</option>
                <option value="self">Self Transaction</option>
              </select>
            </div>
            <div>
              <label
                class="text-gray-800 font-semibold block mt-3 text-md"
                for="transaction_reference"
              >
                Transaction Reference
              </label>
              <input
                value={ref}
                onChange={(e) => {
                  setRef(e.target.value);
                }}
                class="w-full bg-indigo-100 px-4 py-1 rounded-lg focus:outline-none"
                type="text"
                name="transaction_reference"
                id="transaction_reference"
                placeholder="Transaction Reference"
              />
            </div>

            <div>
              <label
                class="text-gray-800 font-semibold block mt-3 text-md"
                for="amount"
              >
                Amount
              </label>
              <input
                value={amount}
                onChange={(e) => {
                  setAmount(e.target.value);
                }}
                class="w-full bg-indigo-100 px-4 py-2 rounded-lg focus:outline-none"
                type="text"
                name="amount"
                id="amount"
                placeholder="Amount in Euro"
              />
            </div>
            <button
              type="submit"
              onClick={(e) => handleSubmit(e)}
              class="w-full mt-4 bg-indigo-500 rounded-lg px-4 py-1 text-lg text-gray-100 tracking-wide font-semibold font-sans"
            >
              Submit
            </button>
            <button
              type="submit"
              class="w-full mt-4 bg-indigo-100 rounded-lg px-4 py-1 text-lg text-gray-800 tracking-wide font-semibold font-sans"
            >
              Clear
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default TransactionForm;
